import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const RoomOverworld = ({ nickname }) => {
  const [roomID, setRoomID] = useState("");
  const [idText, setIDText] = useState("");
  const navigate = useNavigate();

  const confirmRoomID = async (roomID) => {
    const res = await fetch("http://192.168.0.12:5000");
    const data = await res.json();
    return data;
  };
  useEffect(() => {
    if (!nickname) {
      navigate("/");
    }
  });

  const onClick = (e) => {
    navigate("/newRoom", { state: { nickname: nickname, roomID: null } });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setRoomID(idText);
    console.log("ON SUBMIT: ", roomID);
    let data = JSON.stringify(await confirmRoomID(roomID));
    console.log(data);
    navigate("/newRoom", { state: { nickname: nickname, roomID: roomID } });
  };

  return (
    <>
      <div className="container pt-0">
        <h2 className="header pt-0 mt-0 mb-1">üçø Hello {nickname} üçø</h2>
        <form className="form-float" onSubmit={onSubmit}>
          <div className="form-control">
            <input
              type="text"
              placeholder="Room ID"
              onChange={(e) => {
                setIDText(e.target.value);
              }}
              className="mFull-1"
            ></input>
          </div>
          <div className="form-control">
            <input type="submit" value="Enter Room" className="btn btn-block"></input>
          </div>
        </form>
        <h3 className="header">or</h3>
        <div>
          <button type="button" className="btn-block" onClick={onClick}>
            Make a room
          </button>
        </div>
      </div>
    </>
  );
};

export default RoomOverworld;
