import { useNavigate } from "react-router-dom";
import { useState } from "react";

const Nickname = ({ onPickNickname, nickname }) => {
  const navigate = useNavigate();
  const [nicknameError, setnicknameError] = useState(false);
  const onSubmit = (e) => {
    e.preventDefault();

    if (!nickname) {
      setnicknameError(true);
      return;
    }
    console.log("Hello", nickname, "!");
    navigate("/rooms");
  };

  return (
    <>
      <div>
        {nicknameError ? (
          <div className="alert alert-error shadow-lg">
            <div>
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current flex-shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
              <span>Enter a nickname</span>
            </div>
          </div>
        ) : (
          <></>
        )}
        <h1>Pick a nickname to make or join a poll room</h1>

        <form onSubmit={onSubmit}>
          <div className="container mx-auto">
            <input type="text" onChange={(e) => onPickNickname(e.target.value)} placeholder={"choose a nickname"} />
          </div>
          <div>
            <input type="submit" value="enter" className="btn btn-active btn-wide" />
          </div>
        </form>
      </div>
    </>
  );
};

export default Nickname;
