import { io } from "socket.io-client";

export let socket;

export function initSocketConnection(state, participants) {
  socket = io("http://localhost:5000", { autoConnect: false });
  socket.auth  =
  socket.on("connect", () => {
    
    console.log("CONNECTED: ", socket.id, "\n", JSON.stringify({ ...state }));
    socket.emit("join", state.roomID, state.nickname);
  });

  socket.on("disconnect", () => {
    console.log("DISCONNECTED: ", socket.id);
  });

  socket.on("newUser", (nickname) => {
    console.log(nickname, "has joined the room.");
    participants.current = [...participants.current, nickname];
    console.log(participants);
  });

  socket.on("userDisconnect", (socketID) => {
    console.log(socketID, "left.");
  });
}

export function disconnectSocket() {
  socket.off("connect");
  socket.off("disconnect");
  socket.off("pong");
  socket.disconnect();
}
