import React from "react";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import * as Socket from "../services/Socket.js";

const Room = () => {
  const { state } = useLocation();
  const [participants, setParticipants] = useState([state.nickname]);

  const changeParticipants = (newParticipants) => {
    setParticipants(newParticipants);
  };
  useEffect(() => {
    Socket.initSocketConnection(state, participants, changeParticipants);

    return () => {
      Socket.disconnectSocket();
    };
    // eslint-disable-next-line
  }, []);

  return (
    <>
      <div className="container"></div>
      <div className="container">
        <h3>Participants</h3>
        {participants.map((parts) => (
          <li key={parts.socketID}>parts.nickname}</li>
        ))}
        {console.log(participants)}
      </div>
    </>
  );
};

export default Room;
