import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const io = require("socket.io-client");

const RoomOverworld = ({ nickname }) => {
  const [roomID, setRoomID] = useState("");

  const navigate = useNavigate();
  useEffect(() => {
    if (!nickname) {
      navigate("/");
    }
  });

  const onClick = (e) => {
    let socket = io("http://localhost:5000");

    socket.on("connect", () => {
      console.log("CONNECTED: :", socket.id);
    });

    socket.on("disconnect", () => {
      console.log("DISCONNECTED: ", socket.id);
    });
  
    navigate("/newRoom");
    return () => socket.disconnect();
  };
  return (
    <>
      <div className="container pt-0">
        <h2 className="header pt-0 mt-0 mb-1">üçø Hello {nickname} üçø</h2>
        <form className="form-float">
          <div className="form-control">
            <input type="text" placeholder="Room ID" className="mFull-1"></input>
          </div>
          <div className="form-control">
            <input type="submit" value="Enter Room" className="btn btn-block"></input>
          </div>
        </form>
        <h3 className="header">or</h3>
        <div>
          <button type="button" className="btn-block" onClick={onClick}>
            Make a room
          </button>
        </div>
      </div>
    </>
  );
};

export default RoomOverworld;
