import React from "react";
import { useEffect, useState, useRef } from "react";
import { useLocation } from "react-router-dom";
import * as Socket from "../services/Socket.js";

const Room = () => {
  const { state } = useLocation();
  const [participants, setParticipants] = useState([state.nickname]);
  const socketInstance = useRef(null);

  const changeParticipants = (newParticipants) => {
    setParticipants(newParticipants);
  };

  useEffect(() => {
   socketInstance.currnet = Socket.initSocketConnection(state, participants, changeParticipants);

    return () => {
      socket.disconnectSocket();
    };
    // eslint-disable-next-line
  },[]);

  return (
    <>
      <div className="container">
        <h3>Movie Pool </h3>
      </div>
      <div className="container">
        <h3>Participants</h3>
        <div className="name-list">
          {participants.map((part, index) => (
            <li key={index}>{part}</li>
          ))}
        </div>
      </div>
    </>
  );
};

export default Room;
